import axios from "axios"

function UpdateTask (props) {

    const {formData, setFormData, status} = props

    const handleSubmit = () => {
        return null
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <fieldset>
                    <legend>Update Task</legend>

                    <fieldset>
                        <input type="text" name="task_id" placeholder="task ID" />
                        <p>OR</p>
                        <input type="text" name="task_title" placeholder="task Title" />
                    </fieldset>
                    <fieldset>
                        <select name='status' value={formData.status}> 
                            {status.map(status => (
                                <option key={status.value} value={status.value}>
                                    {status.label}
                                </option>
                            ))}
                        </select>
                    </fieldset>
                </fieldset>
            </form>
        </>
    );
}

export default UpdateTask
