import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AddTask from './AddTask';
import './static/main.css'

function Main() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        status: ''
    });

    const [status, setStatus] = useState([]);
    useEffect(() => {
        const fetchStatus = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/statusOptions');
                setStatus(response.data);
                setFormData(formData => ({ ...formData, status: response.data[0]?.value || '' }));
            } catch (error) {
                console.error('Error fetching status_options: ', error);
            }
        };
        fetchStatus();

    }, []);

    return (
        <section>
            <p>This is the Main Content</p>
            <br/>

            {formData.title != null ? (
                <div className='showData'>
                    <p>{formData.title}</p>
                    <p>{formData.description}</p>
                    <p>{formData.status}</p>
                    <p></p>
                </div>
            ) : null}

            <br/>
            <AddTask formData={formData} setFormData={setFormData} status={status} />
        </section>
    );
}

export default Main;
