import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Main() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        status: ''
    });

    const [status, setStatus] = useState([]);
    useEffect(() => {
        const fetchStatus = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/statusOptions');
                setStatus(response.data);
                setFormData(formData => ({ ...formData, status: response.data[0]?.value || '' }));
            } catch (error) {
                console.error('Error fetching status_options: ', error);
            }
        };
        fetchStatus();

    }, []);

    const handleChange = (e) => {
        const{name, value} = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/add', formData)
            console.log("Response from Flask: ", response.data)
        } catch (error) {
            console.error("Error sending data: ", error);
        }
    }

    return (
        <section>
            <p>This is the Main Content</p>
            <br/>
            <form onSubmit={handleSubmit}>
                <fieldset>
                    <legend>Add Tasks</legend>
                    <input type='text' name='title' value={formData.title} placeholder="Task Title" onChange={handleChange}/>
                    <input type='text' name='description' value={formData.description} placeholder='Description' onChange={handleChange}/>
                    <select name='status' value={formData.status} onChange={handleChange}> 
                        {status.map(status => (
                            <option key={status.value} value={status.value}>
                                {status.label}
                            </option>
                        ))}
                    </select>
                    <input type="submit" value="Add"/>
                </fieldset>
            </form>
        </section>
    );
}

export default Main;
